import React,{useContext} from 'react'
import { Route, Switch } from 'react-router-dom';
import SlidBar from './Components/Slidbar'
import Home from './pages/Home'
import About from './pages/About'
import GuestContext from './Context/guestContext/guestContext'
import ThemDark from './Components/ThemDark'
import {
    CSSTransition,
    TransitionGroup,
  } from 'react-transition-group';
  
function Main() {
    const {backMode} = useContext(GuestContext)

    return (
        <div className="flex ">

        {/*Slider Bar */}
        <div className="w-0 sm:w-0 lg:w-20 ">
        <ThemDark/>
          <div className={`flex-shrink-0 h-screen overflow-hidden bg-slidbarcolor p-3 && ${ backMode === "light"? 'bg-slidbarcolor ' : 'bg-white'}`}>
            <SlidBar/>
            
          </div>
        </div>
 
        {/*Route all of pages*/}
        <div className="w-full ">
           <div className={`h-screen overflow-y-auto && ${ backMode === "light"? 'bg-pagescolor ' : 'bg-white'}`}>
           <Route render={({location}) => (
          <TransitionGroup>
            <CSSTransition
              key={location.key}
              timeout={600}
              classNames="fade"
            >
              <Switch location={location}>
              <Route exact path='/' component={Home} />
              <Route exact path='/about' component={About} />
              </Switch>
            </CSSTransition>
          </TransitionGroup>
        )} />
           
          </div>
        </div>
      </div>
    )
}

export default Main
